import 'bootstrap/dist/css/bootstrap.min.css';
import React from'react';
import Note from "./Note";
import ModalComponent from "./ModalComponent";
import { useQuery}  from '@tanstack/react-query';
import {postToDb } from './api/CreateToDo';
import { deleteFromDb } from './api/DeleteToDO';
import axios from 'axios'
function App() { 

  const { isLoading, error, data, isFetching } = useQuery({
    queryKey: ['todos'],
    queryFn: () =>
      axios
        .get("http://localhost:8000")
        .then((res) => res.data),
  });
  if (isLoading) {return 'Loading...'}

  if (error) return 'An error has occurred: ' + error.message



  return (

<div className="App">
{console.log("RAN",data)}
  <ModalComponent addToDos={postToDb}/>
      {
          data.map((noteItem) => {
          return <Note note = {noteItem.todos} onDelete = {deleteFromDb} key={noteItem._id} id={noteItem._id}/>




      })}
</div>
  );

}

export default App;
