import React from "react";
import {deleteFromDb} from './api/DeleteToDO';
import {useMutation,useQueryClient}  from '@tanstack/react-query';
import axios from 'axios';
import { useQuery}  from '@tanstack/react-query';



function Note(props) {
  const { isFetching } = useQuery({
    queryKey: ['todos']});
  
 console.log(props)
  function handleClick(item) {}
  const queryClient = useQueryClient();
  const addTodoMutation = useMutation({
    mutationFn:  deleteFromDb,
    onMutate: async (id) => {
     await queryClient.cancelQueries({ queryKey: ['todos'] })
     const previousTodos = queryClient.getQueryData(['todos'])
    queryClient.setQueryData(['todos'], (old) => old.filter(t => t._id!==id))
    return { previousTodos }
  }, 
  onError: (err, newTodo, context) => {
    queryClient.setQueryData(['todos'], context.previousTodos)
  },
  // Always refetch after error or success:
  onSettled: () => {
    queryClient.invalidateQueries({ queryKey: ['todos'] })
    

  },

})
  
const handleDelete = (id) => {
  addTodoMutation.mutate(id)
}



  return (
    <div className="note">
      {  props.note.map((item) => {
        return <ul> <li onClick={() => {handleClick(item)}} key={item._id}>{item.title}</li></ul>
      })}
      <button style = {{display: isFetching?"":""}} onClick={() => {handleDelete(props.id)}}>DELETE</button>
    </div>
  );
}


export default Note;
