import React from "react";
import {deleteFromDb} from './api/DeleteToDO';
import {useMutation,useQueryClient}  from '@tanstack/react-query';
import axios from 'axios';
import { useQuery}  from '@tanstack/react-query';


function Note(props) {
 const styles = {
  display:"block"
 }
  function handleClick(item) {}
  const { isLoading } = useQuery()
  const queryClient = useQueryClient();
  const addTodoMutation = useMutation({
    mutationFn:  deleteFromDb,
    onMutate: async (id) => {
     await queryClient.cancelQueries({ queryKey: ['todos'] })
     const previousTodos = queryClient.getQueryData(['todos'])
    queryClient.setQueryData(['todos'], (old) => old.filter(t => t._id!==id))
    return { previousTodos }
  }, 
})
  
const handleDelete = (id) => {
  addTodoMutation.mutate(id)
}

if(isLoading) {
     styles.display ="none"
}

  return (
    <div className="note">
      {  props.note.map((item) => {
        return <ul> <li onClick={() => {handleClick(item)}} key={item._id}>{item.title}</li></ul>
      })}
      <button style = {{styles}}onClick={() => {handleDelete(props.id)}}>DELETE</button>
    </div>
  );
}


export default Note;
