},[]);

return (

<div className="App">

    <>
      <Button variant="primary" onClick={() => setModalShow(true)}>
        Launch modal with grid
      </Button>

      <MydModalWithGrid show={modalShow} onHide={() => setModalShow(false)} onAdd={addItem} />
    </>
          {items.map((todoItem, index) => (
            <TodoItem
              key={index}
              id={index}
              text={todoItem}
              onChecked={deleteItem}
            />
          ))}
      {
          todos[2].map((noteItem) => {
          console.log(noteItem)

           return <Note title = {noteItem.title}/>




      })}
</div>
  );

}

export default App;
