import React from'react';
import { useState, useEffect } from 'react';
import Note from "./Note";
import ModalComponent from "./ModalComponent";
import axios from "axios";

import 'bootstrap/dist/css/bootstrap.min.css';




function App() { 
const [todos,setTodos] = useState([{title:"jj"}])


const [loading,setLoading] = useState("false");

useEffect(() => {
  axios
.get("http://localhost:8000")
.then(data => console.log(data.data.todos))
.catch(error => console.log(error));


setLoading(true)
},[]);
  function addToDos(todo) {
  setTodos(prev => {
   return [...prev,
     [...todo]
  ]})
    console.log("FROMAPP",todo); 
    postToDb(todo);

  }
  function postToDb(todo) {
    console.log("posting to db",todo);
    axios.post(`http://localhost:8000`, {
      todo
    })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });


  }

  function deleteNote(id) {

    setTodos(prevNotes => {
      return prevNotes.filter((noteItem, index) => {
        return index !== id;
      });
    });
    postToDb(id);
  }






return (

<div className="App">

  <ModalComponent addToDos={addToDos}/>
      {
          todos.map((noteItem,index) => {

           console.log(todos);
           return <Note note = {noteItem} onDelete = {deleteNote} key={index} id={index}/>




      })}
</div>
  );

}

export default App;
