import React from "react";
import {deleteFromDb} from './api/DeleteToDO';
import {useMutation,useQueryClient}  from '@tanstack/react-query';

function Note(props) {
  const queryClient = useQueryClient();
  function handleClick(item) {
    console.log("clicked item",item)
 if(item.completed==="true"){ item.completed="false"}
 if(item.completed==="false"){ item.completed="true"}
 console.log("clicked item after",item)
  }
  
  const { mutate: Delete } = useMutation(
    (id) => deleteFromDb(id),
    {
      onSettled: () => {
        queryClient.invalidateQueries('todos');
      },
    }
  );



  return (
    <div className="note">
    
          {  props.note.map((item) => {
         
      return <ul> <li onClick={() => {handleClick(item)}} key={item._id}>{item.title}</li></ul>
      })}
 <button onClick={() => {handleDelete(props.id)}}>DELETE</button>

      
    </div>
  );
}


export default Note;
