import axios from "axios";
export const  postToDb = (todo) => {
    console.log("posting to db",todo);
    axios.post(`http://localhost:8000`, {
      todo
    })
      .then((res) => res.data)
      .catch(function (error) {
        console.log(error);
      });
        
     
  }
