import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Modal from 'react-bootstrap/Modal';
import Row from 'react-bootstrap/Row';
import ToDoItem from './TodoItem';
import InputArea from './InputArea';
import {useMutation,useQueryClient}  from '@tanstack/react-query';
import {postToDb} from './api/CreateToDo'
import {deleteFromDb} from './api/DeleteToDO'
function ModalComponent(p){


const  MydModalWithGrid = (props) => {     
  const queryClient = useQueryClient();
  const Create = useMutation({
    mutationFn: postToDb,
    
      onSuccess: (data) => {
        queryClient.setQueryData(['todos',data],data)
        queryClient.invalidateQueries(['todos']);
      },
    }
  );
  // const createPostMutation = useMutation({
  //   mutationFn: createPost,
  //   onSuccess: data => {
  //     queryClient.setQueryData(["posts", data.id], data)
  //     queryClient.invalidateQueries(["posts"], { exact: true })
  //     setCurrentPage(<Post id={data.id} />)
  //   },
  // })

const [items, setItems] = useState([]);

  
function addItem(title) {
   if(!title){
     alert("Please add a todo");
}
else 
{    setItems(prevItems => {
      return [...prevItems, {title:title, completed:"false",}];
    });}
   
   
  }
 function addnote() {
  console.log("RAN in addnote")
  Create(
        items
       )
      setItems([]);
       }

  function deleteItem(id) {
    setItems(prevItems => {
      return prevItems.filter((item, index) => {
        return index !== id;
      });
    });
  }

  return (
    <Modal {...props} aria-labelledby="contained-modal-title-vcenter">
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
         Add a todo list:
        </Modal.Title>
      </Modal.Header>
      <Modal.Body className="show-grid">
        <Container>
          <Row className='form'>
            <Col xs={12} md={12}>
         <InputArea onAdd={addItem} />
            </Col>
          </Row>
           <Row>
            { items.map((todoItem, index) => (
             
             <ToDoItem
              id = {index}
              key={index}
              text={todoItem.title}
                onChecked={deleteItem}
                
            />
          ))}
          </Row>
        </Container>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={() =>{ addnote(); props.onHide()}}>Add</Button>
      </Modal.Footer>
    </Modal>
  );
  }

console.log(p);

const [modalShow, setModalShow] = useState(false);
return (
   <div>
    <>
      <Button variant="primary" onClick={() => setModalShow(true)}>
      +Add  new Todo's 
      </Button>

      <MydModalWithGrid show={modalShow} onHide={() => setModalShow(false)}  />
    
    </>
   
  </div>

)}
export default ModalComponent
