const addTodoDeletion = useMutation({
  mutationFn:  postToDb,
  onMutate: async (id) => {
   await queryClient.cancelQueries({ queryKey: ['todos'] })
   const previousTodos = queryClient.getQueryData(['todos'])
  queryClient.setQueryData(['todos'], (old) =>old?.filter((t) => t.id !== id) )
  return { previousTodos }
}})


  return (
    <div className="note">
      {  props.note.map((item) => {
        return <ul> <li onClick={() => {handleClick(item)}} key={item._id}>{item.title}</li></ul>
      })}
      <button onClick={() => {addTodoDeletion.mutate(props.id)}}>DELETE</button>
    </div>
  );
}


export default Note;
