return (
   <div>
    <>
      <Button variant="primary" onClick={() => setModalShow(true)}>
      +Add  new Todo's 
      </Button>

      <MydModalWithGrid show={modalShow} onHide={() => setModalShow(false)}  />
    
    </>
   
  </div>

)}
export default ModalComponent
