});
  return (
    <div className="note">
      {  props.note.map((item) => {
        return <ul> <li onClick={() => {handleClick(item)}} key={item._id}>{item.title}</li></ul>
      })}
      <button onClick={() => {handleDelete(props.id)}}>DELETE</button>
    </div>
  );
}


export default Note;
