import { useState, useEffect } from 'react';
import Note from "./Note";
import ModalComponent from "./ModalComponent";
import axios from "axios";

import 'bootstrap/dist/css/bootstrap.min.css';




function App() { 
const [todos,setTodos] = useState([
[{id:1, title: "delectus aut autem",completed: false},{ id: 2, title: 'quis ut nam facilis et officia qui', completed: false}],
[{id: 3, title: 'Watch a classic movie', completed: false, userId: 4},{id: 4, title: 'Contribute code or a monetary donation to an open-source software project', completed: false, userId: 48},{id: 5, title: "Solve a Rubik's cube", completed: false, userId: 31},{id: 6, title: 'Bake pastries for me and neighbor', completed: false, userId: 39}],
 [{id:7,title: 'Go see a Broadway production', completed: false, userId: 32},{id: 8, title: 'Write a thank you letter to an influential person in my life', completed: true, userId: 13},{id: 9, title: 'Invite some friends over for a game night', completed: false, userId: 47},{id: 10, title: 'Have a football scrimmage with some friends', completed: false, userId: 19}],
]);


  function addToDos(todo) {
    console.log("RAN")
console.log("TODO",todo);
setTodos(prev => {
   return [...prev,
     [...todo]
  ]})
    console.log("FROMAPP",todo); 
    postToDb();

  }
  function postToDb(id="") {

    axios.post(`http://localhost:8000/${id}`, {
      todos
    })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });

  }

  function deleteNote(id) {

    setTodos(prevNotes => {
      return prevNotes.filter((noteItem, index) => {
        return index !== id;
      });
    });
    postToDb(id);
  }


