import React from "react";
import {deleteFromDb} from './api/DeleteToDO';
import {useMutation,useQueryClient}  from '@tanstack/react-query';

function Note(props) {

  function handleClick(item) {}
 
  const queryClient = useQueryClient();
  
//   const addTodoDeletion = useMutation({
//     mutationFn:  deleteFromDb,
//     onMutate: async (id) => {
//       console.log("id",id)
//      await queryClient.cancelQueries({ queryKey: ['todos',id] })
//      const previousTodos = queryClient.getQueryData(['todos',id])
//     queryClient.setQueryData(['todos',id], old=> old.filter((t) => t.id!==id));
//     return { previousTodos }
//   },

//   // If the mutation fails,  
//   // use the context returned from onMutate to roll back
//   onError: (err, {id}, context) => {
//     queryClient.setQueryData(['todos',id], context.previousTodos)
//   },
//   // Always refetch after error or success:
//   onSettled: ({id}) => {
//     queryClient.invalidateQueries({ queryKey: ['todos',id] })
//   },
// })
const addTodoDeletion =  useMutation(({id}) => axiosClient.delete(`/subtasks/${id}`), {
  // When mutate is called:
  onMutate: async ({id}) => {
    // Cancel any outgoing refetches (so they don't overwrite our optimistic update)
    await queryClient.cancelQueries(['tasks', id])

    // Snapshot the previous value
    const previousTasks = queryClient.getQueryData(['tasks', id])

    // Optimistically update to the new value
    queryClient.setQueryData(['tasks', id], old => old.filter((t) => t.id !== id))

    // Return a context object with the snapshotted value
    return { previousTasks }
  },
  // If the mutation fails, use the context returned from onMutate to roll back
  onError: (err, { id }, context) => {
    queryClient.setQueryData(['tasks', id], context.previousTasks)
  },
  // Always refetch after error or success:
  onSettled: (newData, error, { id }) => {
    queryClient.invalidateQueries(['tasks', id])
  },
})

  return (
    <div className="note">
      {  props.note.map((item) => {
        return <ul> <li onClick={() => {handleClick(item)}} key={item._id}>{item.title}</li></ul>
      })}
      <button onClick={() => {addTodoDeletion.mutate(props.id)}}>DELETE</button>
    </div>
  );
}


export default Note;
